mapper.delete:
DELETE FROM artist
WHERE artist_id = #{artistId}
AND user_id = #{userId}

mapper.select:
SELECT * FROM artist
WHERE artist_id = #{artistId}
AND user_id = #{userId}

mapper.update:
UPDATE artist
SET liked = #{liked}
WHERE artist_id = #{artistId}
AND user_id = #{userId}

mapper.select:
select * from artist
where artist_id = #{artistId}
and user_id = #{userId}
and liked = 1
